import React from 'react';

import Showcase from './Showcase.js';
import './Showcase.css';

import Students_info from '../../assets/info_con.js';

import { useEffect } from "react";
import { motion } from "framer-motion";

const Chaplin = (props) => {

    const pageVariants = {
        initial: { opacity: 0, y: 50 },
        animate: { opacity: 1, y: 0, transition: { duration: 0.3, delay: 0} },
        exit: { opacity: 0, y: -50, transition: { duration: 0.3 , delay: 0} }
      };

      useEffect(() => {
        window.scrollTo(0, 0)
      }, [])

  const sequence = 3;

  return (
    <motion.div 
        className="about_container"
            variants={pageVariants}
            initial="initial"
            animate="animate"
            exit="exit">
      <Showcase 
        designer={Students_info[sequence].designer}
        typeface={Students_info[sequence].typeface}
        styles={Students_info[sequence].styles}
        description={Students_info[sequence].description}
        link = {Students_info[sequence].link}
        next_to={Students_info[sequence].next_to}
        next_designer={Students_info[sequence].next_designer}
        next_typeface={Students_info[sequence].next_typeface}
        cover = {Students_info[sequence].cover}
        img_0_s_a = {Students_info[sequence].showcase[0]}
        img_0_s_b = {Students_info[sequence].showcase[1]}
        img_1_s_a = {Students_info[sequence].showcase[2]}
        img_1_s_b = {Students_info[sequence].showcase[3]}
        img_2_r = {Students_info[sequence].showcase[4]}
        img_3_r = {Students_info[sequence].showcase[5]}
        img_3_s_a = {Students_info[sequence].showcase[6]}
        img_3_s_b = {Students_info[sequence].showcase[7]}
        img_4_r = {Students_info[sequence].showcase[8]}>
      </Showcase>
    </motion.div>
  );
};

export default Chaplin;
